// 浮點字面值測試
int main() {
    // 基本浮點數
    float f1 = 3.14;
    float f2 = 0.5;
    float f3 = 123.456;
    
    // 科學記號
    float scientific1 = 1.23e4;    // 12300
    float scientific2 = 5.67E-3;   // 0.00567
    float scientific3 = 2e10;      // 20000000000
    
    // double 類型
    double d1 = 3.141592653589793;
    double d2 = 1.23e-10;
    
    // 小數點的不同形式
    float dot1 = .5;        // 0.5
    float dot2 = 5.;        // 5.0
    
    return 0;
}